mapper.update:
UPDATE TBL_BOARDGROUP
SET BGNAME=#{bgname}, BGUSED=#{bgused}, BGREPLY=#{bgreply}, BGREADONLY=#{bgreadonly}
WHERE BGNO=#{bgno}

mapper.select:
SELECT BGNO, BGNAME, BGUSED, BGREPLY, BGREADONLY
FROM TBL_BOARDGROUP
WHERE BGDELETEFLAG='N' AND BGNO=#{bgno}

SELECT BGNO, BGNAME, BGUSED, BGREPLY, BGREADONLY
FROM TBL_BOARDGROUP
WHERE BGDELETEFLAG='N' AND BGNO=#{bgno}

mapper.delete:
UPDATE TBL_BOARDGROUP
SET BGDELETEFLAG='Y'
WHERE BGNO=#{bgno}
